INSERT INTO `video` (`id`, `introduction`, `link`, `name`, `poster`) VALUES
	(1, '本片由哔哩哔哩与中国文物报社联合出品。历史留下了丰富的革命文物，它们每一件都讲述着一个故事，见证着一段历史，折射着一种精神。我们将精选从1921年中国共产党成立，至1949年中华人民共和国成立这段时间内，8件（套）不同时期的代表性文物，用它们的故事，相关的人物，呈现那段革命历程中最重要的事件、转折点、决定性瞬间。以现代的表达方式让年轻人了解中国共产党人追求真理、舍生取义、百折不挠的伟大精神，让年长者换一个新角度重新看待那段历史。', 'BV1oM4y1T7rq', '烈火，鲜血与旗帜', 'https://mypic-1305118058.cos.ap-hongkong.myqcloud.com/img/烈火，鲜血与旗帜.jpg'),
	(2, '他是五大书记中最年轻的一位，叶帅称他是党的骆驼，中国人民的骆驼。他人生中有怎样的两大憾事？他管理不好自己的小家，甚至连人民币都不认识，竟然成为建国前后管理城市的中共精英？他是中共五老之一，他的诗，如何改变了儿子的一生？他一生爱国坚贞不渝，敢于直言极谏，凭什么获赞“文胆剑心”？他竟用大刀对抗大炮，让数百日军头颅落地，勇气从何而来？……他们的儿女们深情回忆前辈曾被历史雪藏的稀有故事，呈现给人们一部恢宏的史诗，在人们心中立起一座座不朽的丰碑。', 'BV1xW411X7dV', '红色家族', 'https://mypic-1305118058.cos.ap-hongkong.myqcloud.com/img/红色家族.jpg'),
	(3, '中国的红色战争时期所经历的峥嵘岁月进行了简明的梳理。', 'BV1Jx411M7nV', '大甲午', 'https://mypic-1305118058.cos.ap-hongkong.myqcloud.com/img/大甲午.png'),
	(4, '该片是第一次以纪录片的方式梳理平川的红色文化。这是一部全景式反映发生在平川大地上的革命历史事件的文献纪录片。该片生动的描绘了1932年爆发的靖远起义（其事件的主要发生地在平川）、1936年红军西征及打拉池会师、上世纪三四十年代我地下党徒水支部革命风云的历史画卷。', 'BV1Wx411M7Bn', '红色平川', 'https://mypic-1305118058.cos.ap-hongkong.myqcloud.com/img/红色平川.jpg');
